import React, { Component } from 'react'

class Welcome extends Component {

  render() {

    const { name } = this.props

    return(
      <div>
        {/* <h1>This is Child Component</h1> */}
        <h3>Welcome, {name}!</h3>
      </div>
    )
  }
}

class App extends Component {  // App is the parent class here

  render() {

    return(
      <div>
        <h1>Hello!!!</h1>
        <hr />
        <Welcome name="Aditya" />
      </div>
    )
  }
}

export default App;
